<?php use yii\helpers\Url; ?><section class="vuivui"><div class="container"><div class="row"><div class="card border-success mb-3 col-md-10 justify-content-md-center"><div class="card-header bg-transparent border-success">Cảm ơn <b><?=$data['khachhang']['fullname']?></b>đã cho <b><?=Yii::$app->request->hostInfo?></b>cơ hội được phục vụ. Nhân viên <b><?=Yii::$app->request->hostInfo?></b>sẽ <b>gửi tin nhắn hoặc gọi điện </b>xác nhận giao hàng cho Anh/Chị.</div><div class="card-body text-success"><h5 class="card-title">Thông tin đặt hàng</h5><div class="table-responsive"><table class="table table-hover"><tbody><tr><td>Người nhận: <b><?=$data['khachhang']['fullname']?></b></td><td>Số điện thoại :<?=$data['khachhang']['phone']?></td></tr><tr><td colspan="2">Địa chỉ nhận hàng: <b><?=$data['khachhang']['address']?>.</b> (Nhân viên sẽ gọi xác nhận trước khi giao)</td></tr><tr><td colspan="2"><?php $thanhtoan=['nha'=>'Thanh toán tại nhà','congty'=>'Thanh toán tại công ty','chuyenkhoan'=>'Thanh toán chuyển khoản']; ?>Hình thức thanh toán : <strong><?=$thanhtoan[$data['khachhang']['payment_orders']]?></strong></td></tr><?php $total=0;foreach($data['cart']as $cart):$total_item=0; ?><?php if(!empty($cart['versions'])):$versions=$cart['versions']; ?><tr><td colspan="4"class="table-primary">Sản phẩm : <a href="<?=Url::to(['product/view','slugCate'=>$cart['slugCate'],'slug'=>$cart['slug']],true)?>"><img style="width:52px"src="<?=$cart['image']?>"width=""alt="<?=$cart['pro_name']?>"class="img-thumbnail"><?=$cart['pro_name']?></a></td></tr><tr><td></td><td colspan="3"class="table-success">Phiên bản : <strong><?=$versions['date']?></strong>Tên : <strong><?=$versions['name']?></strong></td></tr><tr><td>Giá 1:<?php if(($versions['price_sale_1']<=0&&$versions['price_1']<=0)||($versions['price_sale_1']==''&&$versions['price_1']=='')):$total_item=0; ?><span>Yêu cầu báo giá</span><?php elseif($versions['price_sale_1']<=0||$versions['price_sale_1']==''):$total_item=$versions['price_1']*$versions['amount_price_sale_1']; ?><span><?=number_format((int)$versions['price_1'],0,',','.')?></span><?php else:$total_item=$versions['price_sale_1']*$versions['amount_price_sale_1']; ?><span><?=number_format((int)$versions['price_sale_1'],0,',','.')?><sup>đ</sup></span> <span><i><del><?=number_format((int)$versions['price_1'],0,',','.')?><sup>đ</sup></del></i></span><?php endif  ?></td><td>Số lượng :<?php echo $versions['amount_price_sale_1']; ?></td><td>Thành tiền :<?php $total+=$total_item;echo number_format((int)$total_item,0,',','.') ?></td></tr><tr><td>Giá 2:<?php if($versions['price_2']<0):$total_item=0; ?><span>Yêu cầu báo giá</span><?php else:$total_item=$versions['price_2']*$versions['amount_price_2']; ?><span><?=number_format((int)$versions['price_2'],0,',','.')?><sup>đ</sup></span><?php endif  ?></td><td>Số lượng :<?php echo $versions['amount_price_2']; ?></td><td>Thành tiền :<?php $total+=$total_item;echo number_format((int)$total_item,0,',','.') ?></td></tr><tr><td>Giá 3:<?php if($versions['price_3']<0):$total_item=0; ?><span>Yêu cầu báo giá</span><?php else:$total_item=$versions['price_3']*$versions['amount_price_3']; ?><span><?=number_format((int)$versions['price_3'],0,',','.')?><sup>đ</sup></span><?php endif  ?></td><td>Số lượng :<?php echo $versions['amount_price_3']; ?></td><td>Thành tiền :<?php $total+=$total_item;echo number_format((int)$total_item,0,',','.') ?></td></tr><?php else: ?><tr><td colspan="4"class="table-primary">Sản phẩm : <a href="<?=Url::to(['product/view','slugCate'=>$cart['slugCate'],'slug'=>$cart['slug']],true)?>"><img style="width:52px"src="<?=$cart['image']?>"width=""alt="<?=$cart['pro_name']?>"class="img-thumbnail"><?=$cart['pro_name']?></a></td></tr><tr><td colspan="2">Giá :<?php $today=date("Y-m-d");if(($cart['price_sales']<=0&&$cart['price']<=0)||($cart['price_sales']==''&&$cart['price']=='')): ?><span>Yêu cầu báo giá</span><?php elseif($cart['start_sale']!=''&&$cart['end_sale']!=''&&$today>=$cart['start_sale']&&$today<=$cart['end_sale']&&$cart['price_sales']<$cart['price']&&$cart['price_sales']>0):$total_item=$cart['price_sales']*$cart['amount']; ?><span><?=number_format((int)$cart['price_sales'],0,',','.')?><sup>đ</sup></span> <span><i><del><?=number_format((int)$cart['price'],0,',','.')?><sup>đ</sup></del></i></span><?php else:$total_item=(int)$cart['price']*(int)$cart['amount']; ?><span><?=number_format((int)$cart['price'],0,',','.')?><sup>đ</sup></span><?php endif  ?></td><td>Số lượng :<?php echo $cart['amount']; ?></td><td>Thành tiền :<?php $total+=$total_item;echo number_format((int)$total_item,0,',','.') ?></td></tr><?php endif  ?><?php endforeach  ?></tbody></table></div></div><div class="card-footer bg-transparent border-success">Tổng tiền: <strong class="float-right"><?=number_format((int)$total,0,',','.')?>₫</strong></div></div></div><div></div></div></section>