<?php
 namespace quantri\models;use Yii;class Tags extends \yii\db\ActiveRecord{public static function tableName(){return 'tags';}public function rules(){return[[['value','link','slugTag','name'],'required'],[['link'],'integer'],[['type'],'string','max'=>20],[['value','slugTag','name'],'string','max'=>255],[['type','value','link'],'unique','targetAttribute'=>['type','value','link']],];}public function attributeLabels(){return['id'=>'ID','type'=>'Loại','name'=>'Tên thẻ','value'=>'Giá trị','link'=>'Liên kết',];}public function getAllTags($type='product'){return yii\helpers\ArrayHelper::map(self::find()->where('type=:type',[':type'=>$type])->all(),'value','value');}public function getAllTagsBytype($type='product',$link){return self::find()->where('type=:type AND link=:link',[':type'=>$type,':link'=>$link])->indexBy('id')->all();}}