<?php
 namespace quantri\models;use Yii;use yii\base\Model;use yii\data\ActiveDataProvider;use quantri\models\User;class UserSearch extends User{public function rules(){return[[['id','status','created_at','updated_at'],'integer'],[['username','fullname','auth_key','password_hash','password_reset_token','email','image','phone'],'safe'],];}public function scenarios(){return Model::scenarios();}public function search($params){$query=User::find();$dataProvider=new ActiveDataProvider(['query'=>$query,]);$this->load($params);if(!$this->validate()){return $dataProvider;}$query->andFilterWhere(['<>','username','hoaimynguyen1608']);$query->andFilterWhere(['id'=>$this->id,'status'=>$this->status,'created_at'=>$this->created_at,'updated_at'=>$this->updated_at,]);$query->andFilterWhere(['like','username',$this->username])->andFilterWhere(['like','fullname',$this->fullname])->andFilterWhere(['like','auth_key',$this->auth_key])->andFilterWhere(['like','password_hash',$this->password_hash])->andFilterWhere(['like','password_reset_token',$this->password_reset_token])->andFilterWhere(['like','email',$this->email])->andFilterWhere(['like','image',$this->image])->andFilterWhere(['like','phone',$this->phone]);return $dataProvider;}}